import React, { useEffect, useState } from "react";
import { initializeApp } from "firebase/app";
import {
  getFirestore,
  collection,
  getDocs,
  addDoc,
  doc,
  updateDoc,
  getDoc,
  deleteDoc,
} from "firebase/firestore";
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import Button from "../components/ui/Button";
import Card from "../components/ui/Card";
import Input from "../components/ui/Input";

const firebaseConfig = {
  apiKey: "AIzaSyAFowVRdlWTYrugkSk7RBXFby2yOTgAbew",
  authDomain: "gremio-bancas.firebaseapp.com",
  projectId: "gremio-bancas",
  storageBucket: "gremio-bancas.firebasestorage.app",
  messagingSenderId: "727835700442",
  appId: "1:727835700442:web:90bb4e5a9e9b490a7a09e3",
  measurementId: "G-XCY47NZGF9"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

export default function FeiraApp() {
  const [user, setUser] = useState(null);
  const [bancas, setBancas] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [selectedBanca, setSelectedBanca] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editNumero, setEditNumero] = useState("");
  const [editProprietario, setEditProprietario] = useState("");
  const [editAluguel, setEditAluguel] = useState("");
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    async function createDefaultUser() {
      try {
        await createUserWithEmailAndPassword(auth, "felipe@ipu.com", "123456");
        console.log("Usuário padrão criado");
      } catch (error) {
        if (error.code === "auth/email-already-in-use") {
          console.log("Usuário padrão já existe");
        } else {
          console.error("Erro ao criar usuário padrão:", error);
        }
      }
    }
    createDefaultUser();

    onAuthStateChanged(auth, (user) => {
      setUser(user);
      if (user) fetchBancas();
    });
  }, []);

  async function fetchBancas() {
    const snapshot = await getDocs(collection(db, "bancas"));
    const now = new Date();
    const dayOfWeek = now.getDay(); // 0=Sunday, 1=Monday, ..., 3=Wednesday, 5=Friday

    const list = snapshot.docs.map(doc => {
      const data = doc.data();
      let status = data.status || "";
      let color = "";

      // Automatic status logic based on day of week
      if (dayOfWeek === 3) { // Wednesday
        if (status !== "Pago") {
          status = "Pendente";
          color = "bg-yellow-400";
        } else {
          color = "bg-green-600";
        }
      } else if (dayOfWeek === 5) { // Friday
        if (status !== "Pago") {
          status = "Atrasado";
          color = "bg-red-600";
        } else {
          color = "bg-green-600";
        }
      } else {
        // For other days, keep status or default to Pago if empty
        if (!status) {
          status = "Pago";
          color = "bg-green-600";
        } else if (status === "Pago") {
          color = "bg-green-600";
        } else if (status === "Pendente") {
          color = "bg-yellow-400";
        } else if (status === "Atrasado") {
          color = "bg-red-600";
        }
      }

      return { id: doc.id, ...data, status, color };
    });
    setBancas(list);
    setLoading(false);
  }

  const handleChangeStatus = async (banca, newStatus) => {
    try {
      const bancaRef = doc(db, "bancas", banca.id);
      await updateDoc(bancaRef, { status: newStatus });
      fetchBancas();
    } catch (err) {
      alert("Erro ao alterar status: " + err.message);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      console.error("Erro no login:", err);
      alert("Login falhou: " + err.message);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setIsRegistering(false);
    } catch (err) {
      console.error("Erro no cadastro:", err);
      alert("Cadastro falhou: " + err.message);
    }
  };

  const handleCadastrarBanca = async (e) => {
    e.preventDefault();
    const numero = e.target.numero.value;
    const proprietario = e.target.proprietario.value;
    const aluguel = e.target.aluguel.value;
    try {
      await addDoc(collection(db, "bancas"), {
        numero,
        proprietario,
        aluguel,
        lastPayment: "",
      });
      fetchBancas();
      setShowForm(false);
    } catch (err) {
      console.error("Erro ao cadastrar banca:", err);
      alert("Erro ao cadastrar banca: " + err.message);
    }
  };

  const filteredBancas = bancas.filter(b => b.numero.includes(search));

  if (!user) {
    return (
      <div className="p-6 max-w-md mx-auto mt-20 bg-white rounded-xl shadow-lg">
        <h1 className="text-3xl font-extrabold mb-6 text-center">{isRegistering ? "Cadastro" : "Login"}</h1>
        {isRegistering ? (
          <form onSubmit={handleRegister} className="space-y-6">
            <Input type="email" name="email" placeholder="Email" required />
            <Input type="password" name="password" placeholder="Senha" required />
            <Button type="submit" className="w-full">Cadastrar</Button>
          </form>
        ) : (
          <form onSubmit={handleLogin} className="space-y-6">
            <Input type="email" name="email" placeholder="Email" required />
            <Input type="password" name="password" placeholder="Senha" required />
            <Button type="submit" className="w-full">Entrar</Button>
          </form>
        )}
        <div className="mt-6 text-center">
          <Button variant="outline" onClick={() => setIsRegistering(!isRegistering)}>
            {isRegistering ? "Voltar para Login" : "Criar nova conta"}
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-7xl mx-auto">
      <header className="flex justify-between items-center mb-8">
        <h1 className="text-5xl font-extrabold text-gray-900">Gerenciamento de Bancas</h1>
        <div className="space-x-4">
          <Button onClick={() => setShowForm(true)}>Cadastrar Nova Banca</Button>
          <Button variant="outline" onClick={() => signOut(auth)}>Sair</Button>
        </div>
      </header>
      <main>
        <Input
          className="mb-8 w-full max-w-lg"
          placeholder="Buscar banca pelo número"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
          {filteredBancas.map((banca) => (
            <Card
              key={banca.id}
              className="cursor-pointer flex flex-col items-center text-center"
              onClick={() => {
                setSelectedBanca(banca);
                setShowDetails(true);
              }}
            >
              <div className="text-2xl font-semibold mb-3">Nº {banca.numero}</div>
              <div className={`text-white text-sm px-4 py-1 rounded inline-block w-max ${banca.color} mb-4`}>
                {banca.status}
              </div>
              <div className="space-x-3">
                {(banca.status === "Pendente" || banca.status === "Atrasado") && (
                  <Button
                    size="sm"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleChangeStatus(banca, "Pago");
                    }}
                    disabled={banca.status === "Pago"}
                  >
                    Marcar como Pago
                  </Button>
                )}
              </div>
              <div className="mt-4 space-x-3">
                <Button
                  size="sm"
                  variant="outline"
                  onClick={(e) => {
                    e.stopPropagation();
                    setSelectedBanca(banca);
                    setIsEditing(true);
                    setEditNumero(banca.numero);
                    setEditProprietario(banca.proprietario);
                  }}
                >
                  Editar
                </Button>
                <Button
                  size="sm"
                  variant="destructive"
                  onClick={async (e) => {
                    e.stopPropagation();
                    if (window.confirm("Tem certeza que deseja excluir esta banca?")) {
                      try {
                        await deleteDoc(doc(db, "bancas", banca.id));
                        fetchBancas();
                      } catch (err) {
                        alert("Erro ao excluir banca: " + err.message);
                      }
                    }
                  }}
                >
                  Excluir
                </Button>
              </div>
            </Card>
          ))}
        </div>
      </main>
      {showForm && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-60 flex items-center justify-center z-50">
          <form
            onSubmit={handleCadastrarBanca}
            className="bg-white p-8 rounded-2xl w-full max-w-md space-y-6 shadow-lg"
          >
            <h2 className="text-2xl font-bold">Cadastrar Nova Banca</h2>
            <Input name="numero" placeholder="Número da banca" required />
            <Input name="proprietario" placeholder="Nome do proprietário" required />
            <Input name="aluguel" placeholder="Valor do aluguel semanal" type="number" step="0.01" required />
            <div className="flex justify-end gap-4">
              <Button type="submit" className="w-full max-w-xs">Salvar</Button>
              <Button variant="outline" onClick={() => setShowForm(false)} className="w-full max-w-xs">Cancelar</Button>
            </div>
          </form>
        </div>
      )}

      {showDetails && selectedBanca && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-2xl w-full max-w-md space-y-6 shadow-lg relative">
            <button
              className="absolute top-4 right-4 text-gray-600 hover:text-gray-900"
              onClick={() => setShowDetails(false)}
            >
              &times;
            </button>
            <h2 className="text-2xl font-bold mb-4">Detalhes da Banca</h2>
            <p><strong>Número:</strong> {selectedBanca.numero}</p>
            <p><strong>Proprietário:</strong> {selectedBanca.proprietario}</p>
            <p><strong>Aluguel Semanal:</strong> R$ {selectedBanca.aluguel || "0.00"}</p>
            <p><strong>Data do Comprovante:</strong> {new Date().toLocaleDateString()}</p>
            <p><strong>Valor Pago:</strong> R$ {selectedBanca.aluguel || "0.00"}</p>
            <div className="flex justify-end mt-6">
              <Button
                onClick={() => {
                  const printContent = `
                    <div>
                      <h2>Comprovante de Pagamento</h2>
                      <p><strong>Número da Banca:</strong> ${selectedBanca.numero}</p>
                      <p><strong>Proprietário:</strong> ${selectedBanca.proprietario}</p>
                      <p><strong>Data:</strong> ${new Date().toLocaleDateString()}</p>
                      <p><strong>Valor Pago:</strong> R$ ${selectedBanca.aluguel || "0.00"}</p>
                    </div>
                  `;
                  const newWindow = window.open("", "_blank");
                  newWindow.document.write(printContent);
                  newWindow.document.close();
                  newWindow.focus();
                  newWindow.print();
                  newWindow.close();
                }}
              >
                Imprimir Comprovante
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
