import React, { useEffect, useState } from "react";
import { initializeApp } from "firebase/app";
import {
  getFirestore,
  collection,
  getDocs,
  addDoc,
  doc,
  updateDoc,
  getDoc,
} from "firebase/firestore";
import {
  getAuth,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import Button from "../components/ui/Button";
import Card from "../components/ui/Card";
import Input from "../components/ui/Input";

const firebaseConfig = {
  apiKey: "AIzaSyAFowVRdlWTYrugkSk7RBXFby2yOTgAbew",
  authDomain: "gremio-bancas.firebaseapp.com",
  projectId: "gremio-bancas",
  storageBucket: "gremio-bancas.firebasestorage.app",
  messagingSenderId: "727835700442",
  appId: "1:727835700442:web:90bb4e5a9e9b490a7a09e3",
  measurementId: "G-XCY47NZGF9"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

export default function FeiraApp() {
  const [user, setUser] = useState(null);
  const [bancas, setBancas] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [selectedBanca, setSelectedBanca] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);

  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      setUser(user);
      if (user) fetchBancas();
    });
  }, []);

  async function fetchBancas() {
    const snapshot = await getDocs(collection(db, "bancas"));
    const now = new Date();
    const quarta = new Date();
    quarta.setDate(quarta.getDate() - quarta.getDay() + 3);
    quarta.setHours(0, 0, 0, 0);
    const quinta = new Date(quarta);
    quinta.setDate(quinta.getDate() + 1);

    const list = snapshot.docs.map(doc => {
      const data = doc.data();
      let status = "";
      let color = "";
      const lastPayment = data.lastPayment ? new Date(data.lastPayment) : null;
      if (!lastPayment || now > quinta && (!data.lastPayment || new Date(data.lastPayment) < quarta)) {
        status = "Atrasado";
        color = "bg-red-500";
      } else if (now >= quarta && now <= quinta && (!data.lastPayment || new Date(data.lastPayment) < quarta)) {
        status = "Pendente";
        color = "bg-yellow-400";
      } else {
        status = "Pago";
        color = "bg-green-500";
      }
      return { id: doc.id, ...data, status, color };
    });
    setBancas(list);
    setLoading(false);
  }

  const handleLogin = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      alert("Login falhou");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setIsRegistering(false);
    } catch (err) {
      alert("Cadastro falhou");
    }
  };

  const handleCadastrarBanca = async (e) => {
    e.preventDefault();
    const numero = e.target.numero.value;
    const proprietario = e.target.proprietario.value;
    try {
      await addDoc(collection(db, "bancas"), {
        numero,
        proprietario,
        lastPayment: "",
      });
      fetchBancas();
      setShowForm(false);
    } catch (err) {
      alert("Erro ao cadastrar banca");
    }
  };

  const filteredBancas = bancas.filter(b => b.numero.includes(search));

  if (!user) {
    return (
      <div className="p-4 max-w-md mx-auto">
        <h1 className="text-2xl font-bold mb-4">{isRegistering ? "Cadastro" : "Login"}</h1>
        {isRegistering ? (
          <form onSubmit={handleRegister} className="space-y-4">
            <Input type="email" name="email" placeholder="Email" required />
            <Input type="password" name="password" placeholder="Senha" required />
            <Button type="submit">Cadastrar</Button>
          </form>
        ) : (
          <form onSubmit={handleLogin} className="space-y-4">
            <Input type="email" name="email" placeholder="Email" required />
            <Input type="password" name="password" placeholder="Senha" required />
            <Button type="submit">Entrar</Button>
          </form>
        )}
        <div className="mt-4 text-center">
          <Button variant="outline" onClick={() => setIsRegistering(!isRegistering)}>
            {isRegistering ? "Voltar para Login" : "Criar nova conta"}
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">Gerenciamento de Bancas</h1>
        <div className="space-x-2">
          <Button onClick={() => setShowForm(true)}>Cadastrar Nova Banca</Button>
          <Button onClick={() => signOut(auth)}>Sair</Button>
        </div>
      </div>

      <Input
        className="mb-4"
        placeholder="Buscar banca pelo número"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {filteredBancas.map((banca) => (
          <Card
            key={banca.id}
            className={`cursor-pointer ${banca.color}`}
            onClick={() => setSelectedBanca(banca)}
          >
            <div className="p-4 text-white font-bold">
              Nº {banca.numero}
              <div className="text-sm">{banca.status}</div>
            </div>
          </Card>
        ))}
      </div>

      {selectedBanca && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl w-full max-w-md">
            <h2 className="text-xl font-bold mb-2">Banca Nº {selectedBanca.numero}</h2>
            <p>Proprietário: {selectedBanca.proprietario}</p>
            <p>Status: {selectedBanca.status}</p>
            <p>Último pagamento: {selectedBanca.lastPayment || "Nunca pago"}</p>
            <Button onClick={() => window.print()}>Imprimir comprovante</Button>
            <Button variant="outline" className="mt-2" onClick={() => setSelectedBanca(null)}>
              Fechar
            </Button>
          </div>
        </div>
      )}

      {showForm && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center z-50">
          <form
            onSubmit={handleCadastrarBanca}
            className="bg-white p-6 rounded-xl w-full max-w-md space-y-4"
          >
            <h2 className="text-xl font-bold">Cadastrar Nova Banca</h2>
            <Input name="numero" placeholder="Número da banca" required />
            <Input name="proprietario" placeholder="Nome do proprietário" required />
            <div className="flex justify-end gap-2">
              <Button type="submit">Salvar</Button>
              <Button variant="outline" onClick={() => setShowForm(false)}>Cancelar</Button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
