import React, { useEffect, useState } from "react";
import { initializeApp } from "firebase/app";
import {
  getFirestore,
  collection,
  getDocs,
  addDoc,
  doc,
  updateDoc,
  getDoc,
} from "firebase/firestore";
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import Button from "../components/ui/Button";
import Card from "../components/ui/Card";
import Input from "../components/ui/Input";

const firebaseConfig = {
  apiKey: "AIzaSyAFowVRdlWTYrugkSk7RBXFby2yOTgAbew",
  authDomain: "gremio-bancas.firebaseapp.com",
  projectId: "gremio-bancas",
  storageBucket: "gremio-bancas.firebasestorage.app",
  messagingSenderId: "727835700442",
  appId: "1:727835700442:web:90bb4e5a9e9b490a7a09e3",
  measurementId: "G-XCY47NZGF9"
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

export default function FeiraApp() {
  const [user, setUser] = useState(null);
  const [bancas, setBancas] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(true);
  const [selectedBanca, setSelectedBanca] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editNumero, setEditNumero] = useState("");
  const [editProprietario, setEditProprietario] = useState("");

  useEffect(() => {
    async function createDefaultUser() {
      try {
        await createUserWithEmailAndPassword(auth, "felipe@ipu.com", "123456");
        console.log("Usuário padrão criado");
      } catch (error) {
        if (error.code === "auth/email-already-in-use") {
          console.log("Usuário padrão já existe");
        } else {
          console.error("Erro ao criar usuário padrão:", error);
        }
      }
    }
    createDefaultUser();

    onAuthStateChanged(auth, (user) => {
      setUser(user);
      if (user) fetchBancas();
    });
  }, []);

  async function fetchBancas() {
    const snapshot = await getDocs(collection(db, "bancas"));
    const now = new Date();
    const quarta = new Date();
    quarta.setDate(quarta.getDate() - quarta.getDay() + 3);
    quarta.setHours(0, 0, 0, 0);
    const quinta = new Date(quarta);
    quinta.setDate(quinta.getDate() + 1);

    const list = snapshot.docs.map(doc => {
      const data = doc.data();
      let status = data.status || "";
      let color = "";

      if (!status) {
        const lastPayment = data.lastPayment ? new Date(data.lastPayment) : null;
        if (!lastPayment || now > quinta && (!data.lastPayment || new Date(data.lastPayment) < quarta)) {
          status = "Atrasado";
          color = "bg-red-500";
        } else if (now >= quarta && now <= quinta && (!data.lastPayment || new Date(data.lastPayment) < quarta)) {
          status = "Pendente";
          color = "bg-yellow-400";
        } else {
          status = "Pago";
          color = "bg-green-500";
        }
      } else {
        if (status === "Pago") {
          color = "bg-green-500";
        } else if (status === "Pendente") {
          color = "bg-yellow-400";
        } else if (status === "Atrasado") {
          color = "bg-red-500";
        }
      }

      return { id: doc.id, ...data, status, color };
    });
    setBancas(list);
    setLoading(false);
  }

  const handleChangeStatus = async (banca, newStatus) => {
    try {
      const bancaRef = doc(db, "bancas", banca.id);
      await updateDoc(bancaRef, { status: newStatus });
      fetchBancas();
    } catch (err) {
      alert("Erro ao alterar status: " + err.message);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      console.error("Erro no login:", err);
      alert("Login falhou: " + err.message);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setIsRegistering(false);
    } catch (err) {
      console.error("Erro no cadastro:", err);
      alert("Cadastro falhou: " + err.message);
    }
  };

  const handleCadastrarBanca = async (e) => {
    e.preventDefault();
    const numero = e.target.numero.value;
    const proprietario = e.target.proprietario.value;
    try {
      await addDoc(collection(db, "bancas"), {
        numero,
        proprietario,
        lastPayment: "",
      });
      fetchBancas();
      setShowForm(false);
    } catch (err) {
      console.error("Erro ao cadastrar banca:", err);
      alert("Erro ao cadastrar banca: " + err.message);
    }
  };

  const filteredBancas = bancas.filter(b => b.numero.includes(search));

  if (!user) {
    return (
      <div className="p-4 max-w-md mx-auto">
        <h1 className="text-2xl font-bold mb-4">{isRegistering ? "Cadastro" : "Login"}</h1>
        {isRegistering ? (
          <form onSubmit={handleRegister} className="space-y-4">
            <Input type="email" name="email" placeholder="Email" required />
            <Input type="password" name="password" placeholder="Senha" required />
            <Button type="submit">Cadastrar</Button>
          </form>
        ) : (
          <form onSubmit={handleLogin} className="space-y-4">
            <Input type="email" name="email" placeholder="Email" required />
            <Input type="password" name="password" placeholder="Senha" required />
            <Button type="submit">Entrar</Button>
          </form>
        )}
        <div className="mt-4 text-center">
          <Button variant="outline" onClick={() => setIsRegistering(!isRegistering)}>
            {isRegistering ? "Voltar para Login" : "Criar nova conta"}
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold">Gerenciamento de Bancas</h1>
        <div className="space-x-2">
          <Button onClick={() => setShowForm(true)}>Cadastrar Nova Banca</Button>
          <Button onClick={() => signOut(auth)}>Sair</Button>
        </div>
      </div>

      <Input
        className="mb-4"
        placeholder="Buscar banca pelo número"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {filteredBancas.map((banca) => (
          <Card
            key={banca.id}
            className={`cursor-pointer`}
            // Remove onClick to avoid conflict with button clicks
          >
            <div className="p-4 font-bold">
              Nº {banca.numero}
              <div className={`text-white text-sm px-2 py-1 rounded mt-2 inline-block ${banca.color}`}>
                {banca.status}
              </div>
              <div className="mt-2 space-x-2">
                {banca.status !== "Pago" && (
                  <Button
                    size="sm"
                    onClick={() => handleChangeStatus(banca, "Pago")}
                  >
                    Marcar como Pago
                  </Button>
                )}
                {banca.status !== "Pendente" && (
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => handleChangeStatus(banca, "Pendente")}
                  >
                    Marcar como Pendente
                  </Button>
                )}
                {banca.status !== "Atrasado" && (
                  <Button
                    size="sm"
                    variant="destructive"
                    onClick={() => handleChangeStatus(banca, "Atrasado")}
                  >
                    Marcar como Atrasado
                  </Button>
                )}
              </div>
            </div>
          </Card>
        ))}
      </div>

      {selectedBanca && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl w-full max-w-md">
            <h2 className="text-xl font-bold mb-2">Banca Nº {selectedBanca.numero}</h2>
            {isEditing ? (
              <form
                onSubmit={async (e) => {
                  e.preventDefault();
                  try {
                    const bancaRef = doc(db, "bancas", selectedBanca.id);
                    await updateDoc(bancaRef, {
                      numero: editNumero,
                      proprietario: editProprietario,
                    });
                    fetchBancas();
                    setIsEditing(false);
                    setSelectedBanca(null);
                  } catch (err) {
                    alert("Erro ao editar banca: " + err.message);
                  }
                }}
                className="space-y-4"
              >
                <Input
                  name="numero"
                  placeholder="Número da banca"
                  value={editNumero}
                  onChange={(e) => setEditNumero(e.target.value)}
                  required
                />
                <Input
                  name="proprietario"
                  placeholder="Nome do proprietário"
                  value={editProprietario}
                  onChange={(e) => setEditProprietario(e.target.value)}
                  required
                />
                <div className="flex justify-end gap-2">
                  <Button type="submit">Salvar</Button>
                  <Button
                    variant="outline"
                    onClick={() => {
                      setIsEditing(false);
                      setSelectedBanca(null);
                    }}
                  >
                    Cancelar
                  </Button>
                </div>
              </form>
            ) : (
              <>
                <p>Proprietário: {selectedBanca.proprietario}</p>
                <p>Status: {selectedBanca.status}</p>
                <p>Último pagamento: {selectedBanca.lastPayment || "Nunca pago"}</p>
                <div className="flex space-x-2 mt-4">
                  <Button
                    onClick={() => {
                      setIsEditing(true);
                      setEditNumero(selectedBanca.numero);
                      setEditProprietario(selectedBanca.proprietario);
                    }}
                  >
                    Editar
                  </Button>
                  <Button
                    variant="outline"
                    onClick={async () => {
                      if (window.confirm("Tem certeza que deseja excluir esta banca?")) {
                        try {
                          await deleteDoc(doc(db, "bancas", selectedBanca.id));
                          fetchBancas();
                          setSelectedBanca(null);
                        } catch (err) {
                          alert("Erro ao excluir banca: " + err.message);
                        }
                      }
                    }}
                  >
                    Excluir
                  </Button>
                  <Button onClick={() => window.print()}>Imprimir comprovante</Button>
                  <Button variant="outline" className="mt-2" onClick={() => setSelectedBanca(null)}>
                    Fechar
                  </Button>
                </div>
              </>
            )}
          </div>
        </div>
      )}

      {showForm && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex items-center justify-center z-50">
          <form
            onSubmit={handleCadastrarBanca}
            className="bg-white p-6 rounded-xl w-full max-w-md space-y-4"
          >
            <h2 className="text-xl font-bold">Cadastrar Nova Banca</h2>
            <Input name="numero" placeholder="Número da banca" required />
            <Input name="proprietario" placeholder="Nome do proprietário" required />
            <div className="flex justify-end gap-2">
              <Button type="submit">Salvar</Button>
              <Button variant="outline" onClick={() => setShowForm(false)}>Cancelar</Button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
