import React from "react";

export default function Input({ type = "text", name, placeholder, value, onChange, required = false, className = "" }) {
  const baseClass = "border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500";

  return (
    <input
      type={type}
      name={name}
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      required={required}
      className={`${baseClass} ${className}`}
    />
  );
}
